"use client";

import { useLanguage } from "@/context/LanguageContext";
import Image from "next/image";
import Link from "next/link";
import { ArrowRight } from "lucide-react";

import { useState, useEffect } from "react";
import { fetchWPPage } from "@/lib/wordpress";
import clsx from "clsx";

export default function OurStoryPage() {
    const { t, locale } = useLanguage();
    const [pageData, setPageData] = useState<any>(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const loadContent = async () => {
            setLoading(true);
            const data = await fetchWPPage('our-story', locale);
            if (data) {
                setPageData(data);
            }
            setLoading(false);
        };
        loadContent();
    }, [locale]);

    // Helper to get array from translations (Fallback)
    interface StoryStep { title: string; desc: string; }
    const steps = (t('story.steps') as unknown) as StoryStep[];

    if (loading) {
        return <div className="min-h-screen flex items-center justify-center text-[var(--coffee-brown)]">Loading...</div>;
    }

    // If WP content exists, render it (Basic HTML rendering for flexibility)
    if (pageData) {
        return (
            <main className="min-h-screen bg-white animate-fade-in">
                {/* Use Featured Image from WP if available, else fallback */}
                <section className="relative h-[60vh] md:h-[80vh] w-full overflow-hidden">
                    <Image
                        src={pageData._embedded?.['wp:featuredmedia']?.[0]?.source_url || "/images/honey-comb.jpg"}
                        alt={pageData.title.rendered}
                        fill
                        className="object-cover"
                    />
                    <div className="absolute inset-0 bg-black/40" />
                    <div className="absolute inset-0 flex flex-col items-center justify-center text-center px-6">
                        <h1 className="text-5xl md:text-8xl font-serif text-white tracking-tight mb-4" dangerouslySetInnerHTML={{ __html: pageData.title.rendered }} />
                    </div>
                </section>

                <section className="py-20 px-6 md:px-20 mx-auto max-w-5xl prose prose-lg prose-headings:font-serif prose-headings:text-[var(--coffee-brown)] text-[var(--coffee-brown)]/80">
                    <div dangerouslySetInnerHTML={{ __html: pageData.content.rendered }} />
                </section>
            </main>
        );
    }

    // FALLBACK to Hardcoded if no WP Page found
    return (
        <main className="min-h-screen bg-white">
            {/* Hero Section */}
            <section className="relative h-[80vh] w-full overflow-hidden">
                <Image
                    src="/images/honey-comb.jpg"
                    alt={t('story.heritage')}
                    fill
                    className="object-cover"
                />
                <div className="absolute inset-0 bg-black/50" />
                <div className="absolute inset-0 flex flex-col items-center justify-center text-center px-6">
                    <span className="text-[var(--honey-gold)] text-sm font-bold uppercase tracking-[0.4em] mb-4">{t('story.heritage')}</span>
                    <h1 className="text-6xl md:text-9xl font-serif text-white tracking-tight mb-8">
                        {t('story.origin')}
                    </h1>
                </div>
            </section>

            {/* Content I: The Philosophy */}
            <section className="py-32 px-6 md:px-20 mx-auto max-w-7xl">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-20 items-center">
                    <div className="space-y-8">
                        <h2 className="text-4xl md:text-5xl font-serif text-[var(--coffee-brown)] leading-tight">
                            {t('story.legacyTitle')}
                        </h2>
                        <p className="text-[var(--coffee-brown)]/70 leading-relaxed text-lg font-light">
                            {t('story.philosophy')}
                        </p>
                        <div className="pt-4">
                            <span className="block text-xs font-bold uppercase tracking-widest text-[var(--coffee-brown)] mb-2">{t('story.signature')}</span>
                            <div className="h-[1px] w-20 bg-[var(--coffee-brown)]" />
                        </div>
                    </div>
                    <div className="relative aspect-[3/4] bg-gray-100">
                        <Image
                            src="/images/hero-1.jpg"
                            alt="The Valley"
                            fill
                            className="object-cover grayscale hover:grayscale-0 transition-all duration-1000"
                        />
                    </div>
                </div>
            </section>

            {/* Content II: The Process */}
            <section className="bg-[var(--coffee-brown)] text-white py-32 px-6">
                <div className="max-w-7xl mx-auto text-center space-y-16">
                    <div className="space-y-6">
                        <span className="text-[var(--honey-gold)] text-xs font-bold uppercase tracking-[0.3em]">{t('story.uncompromising')}</span>
                        <h2 className="text-4xl md:text-6xl font-serif">{t('story.processTitle')}</h2>
                    </div>

                    <div className="grid grid-cols-1 md:grid-cols-3 gap-12 text-left">
                        {Array.isArray(steps) && steps.map((step, idx) => (
                            <div key={idx} className="space-y-4 border-t border-white/10 pt-8 hover:border-[var(--honey-gold)] transition-colors duration-500 group">
                                <h3 className="text-2xl font-serif group-hover:text-[var(--honey-gold)] transition-colors">{step.title}</h3>
                                <p className="text-white/60 leading-relaxed max-w-xs">{step.desc}</p>
                            </div>
                        ))}
                    </div>
                </div>
            </section>

            {/* CTA */}
            <section className="h-[60vh] relative flex items-center justify-center bg-gray-100 overflow-hidden">
                <Image
                    src="/images/coffee-beans.jpg"
                    alt="Texture"
                    fill
                    className="object-cover opacity-20"
                />
                <div className="text-center relative z-10 space-y-8">
                    <h2 className="text-5xl font-serif text-[var(--coffee-brown)]">{t('story.tasteHistory')}</h2>
                    <Link href="/shop" className="inline-flex items-center gap-4 px-12 py-4 bg-[var(--coffee-brown)] text-white uppercase tracking-widest font-bold text-xs hover:bg-[var(--coffee-brown)]/90 transition-all">
                        {t('story.explore')} <ArrowRight size={16} />
                    </Link>
                </div>
            </section>
        </main>
    );
}

import { Metadata } from 'next';
import { notFound } from 'next/navigation';
import ProductDetails from '@/components/shop/ProductDetails';
import ProductSpecs from '@/components/shop/ProductSpecs';
import ProductCarousel from '@/components/shop/ProductCarousel';
import { prisma } from '@/lib/prisma';

async function getProduct(slug: string, locale: string = 'en') {
    try {
        const product = await prisma.product.findUnique({
            where: { slug, isActive: true },
            include: {
                category: true,
                variants: {
                    include: {
                        attributeValues: {
                            include: {
                                attributeValue: {
                                    include: {
                                        attribute: true
                                    }
                                }
                            }
                        }
                    }
                }
            }
        });

        if (!product) return null;

        // Map Prisma product to the shape expected by components
        return {
            id: product.id,
            name: product.name,
            slug: product.slug,
            sku: product.sku || '',
            price: Number(product.price),
            compare_at_price: product.compareAtPrice ? Number(product.compareAtPrice) : null,
            compareAtPrice: product.compareAtPrice ? Number(product.compareAtPrice) : null,
            stock_quantity: product.stockQuantity || 0,
            images: product.images,
            image_url: Array.isArray(product.images) ? product.images[0] : (typeof product.images === 'string' && product.images.startsWith('[') ? JSON.parse(product.images)[0] : product.images),
            category_name: product.category?.name || 'Uncategorized',
            description: product.description || '',
            weight: product.weight ? Number(product.weight) : 0.5,
            width: product.width ? Number(product.width) : null,
            height: product.height ? Number(product.height) : null,
            depth: product.depth ? Number(product.depth) : null,
            costPrice: product.costPrice ? Number(product.costPrice) : null,
            ecotax: product.ecotax ? Number(product.ecotax) : null,
            origin_country: "Yemen",
            taxRate: 0,
            category_slug: product.category?.slug,
            categoryId: product.category?.id,
            variants: product.variants.map(v => ({
                id: v.id,
                name: v.name,
                sku: v.sku,
                price: Number(v.price),
                compareAtPrice: v.compareAtPrice ? Number(v.compareAtPrice) : null,
                stock: v.stock,
                images: v.images,
                attributes: v.attributeValues.map(av => ({
                    id: av.id,
                    attributeId: av.attributeValue.attributeId,
                    value: av.attributeValue.name,
                    name: av.attributeValue.attribute.name
                }))
            }))
        };
    } catch (error) {
        console.error("Error fetching product from Prisma:", error);
        return null;
    }
}

async function getRelatedProducts(categoryId: number | null, currentId: number) {
    if (!categoryId) return [];
    const products = await prisma.product.findMany({
        where: { categoryId, id: { not: currentId }, isActive: true },
        take: 8,
        include: { category: true }
    });
    return products.map(p => ({
        id: p.id,
        name: p.name,
        slug: p.slug,
        price: Number(p.price),
        compare_at_price: p.compareAtPrice ? Number(p.compareAtPrice) : null,
        images: p.images,
        category_name: p.category?.name,
        category_slug: p.category?.slug
    }));
}

async function getNewArrivals() {
    const products = await prisma.product.findMany({
        where: { isActive: true },
        orderBy: { createdAt: 'desc' },
        take: 8,
        include: { category: true }
    });
    return products.map(p => ({
        id: p.id,
        name: p.name,
        slug: p.slug,
        price: Number(p.price),
        compare_at_price: p.compareAtPrice ? Number(p.compareAtPrice) : null,
        images: p.images,
        category_name: p.category?.name,
        category_slug: p.category?.slug
    }));
}

async function getCarriers() {
    try {
        return await prisma.carrier.findMany({
            where: { isActive: true },
            orderBy: { createdAt: 'asc' }
        });
    } catch (error) {
        console.error("Error fetching carriers:", error);
        return [];
    }
}

export async function generateMetadata({ params }: { params: Promise<{ slug: string }> }): Promise<Metadata> {
    const { slug } = await params;
    const product = await getProduct(slug);

    if (!product) {
        return { title: 'Product Not Found | Yemeni Market' };
    }

    let imageUrl = '/images/honey-jar.jpg';
    if (product.images) {
        try {
            const parsed = JSON.parse(product.images);
            if (Array.isArray(parsed) && parsed.length > 0) imageUrl = parsed[0];
        } catch { }
    }

    return {
        title: `${product.name} | Yemeni Market`,
        description: product.description || `Buy authentic ${product.name} from Yemeni Market.`,
        openGraph: {
            title: product.name,
            description: product.description || `Buy authentic ${product.name} from Yemeni Market.`,
            images: [imageUrl],
        },
    };
}

export default async function Page({ params }: { params: Promise<{ slug: string }> }) {
    const { slug } = await params;
    const product = await getProduct(slug);

    if (!product) {
        notFound();
    }

    // Since we spread ...rest in getProduct, we need to ensure the types match what components expect.
    // The components ProductDetails, ProductSpecs, etc. likely take specific interfaces.
    // We should cast 'product' to the expected interface or ensure getProduct returns strict types.
    // For now, let's fix the obvious 'any' casts by trusting the returned structure from strict getProduct.

    const [relatedProducts, newArrivals, carriers] = await Promise.all([
        getRelatedProducts(product.categoryId ?? null, product.id),
        getNewArrivals(),
        getCarriers()
    ]);

    return (
        <main className="min-h-screen">
            <ProductDetails product={product} carriers={carriers} />
            <div className="max-w-7xl mx-auto px-6 pb-20">
                <ProductSpecs
                    weight={product.weight || 0.5}
                    origin={product.origin_country || "Yemen"}
                    shelfLife="24 months"
                    description={product.description || ''}
                    category={product.category_slug || 'honey'}
                />
                <ProductCarousel title="Produits Similaires" products={relatedProducts} />
                <ProductCarousel title="Nouveaut├®s" products={newArrivals} />
            </div>
        </main>
    );
}

"use client";

import { useLanguage } from "@/context/LanguageContext";
import Image from "next/image";
import Link from "next/link";
import { useState, useEffect, useCallback } from "react";
import { ShoppingBag, Star, Filter, Grid, List, Eye } from "lucide-react";
import { useCart } from "@/context/CartContext";
import WishlistButton from "@/components/shop/WishlistButton";
import ProductSkeleton from "@/components/shop/ProductSkeleton";
import CategoryRail from "@/components/shop/CategoryRail";
import { useSearchParams } from "next/navigation";

interface Product {
    id: number;
    name: string;
    price: number;
    slug: string;
    images?: string; // JSON string
    category_name: string;
    category_slug: string;
    description?: string;
    stock_quantity?: number;
    compare_at_price?: number;
}

interface Category {
    id: number;
    name: string;
    slug: string;
}

// ... (imports remain)
import { Suspense } from "react";

// ... (interfaces remain)

function ShopContent() {
    const { t, locale } = useLanguage();
    const { addToCart } = useCart();
    const searchParams = useSearchParams();
    const [products, setProducts] = useState<Product[]>([]);
    const [categories, setCategories] = useState<Category[]>([]);
    const [selectedCategory, setSelectedCategory] = useState("all");
    const [isLoading, setIsLoading] = useState(true);
    const [viewMode, setViewMode] = useState<'grid' | 'list'>('grid');
    const [sortBy, setSortBy] = useState('newest');
    const [minPrice, setMinPrice] = useState("");
    const [maxPrice, setMaxPrice] = useState("");

    // Helper to extract main image from JSON
    const getMainImage = (product: Product): string => {
        try {
            if (!product.images) return '/images/honey-jar.jpg';

            // Check if it's already a clean URL (legacy/csv import support)
            if (product.images.startsWith('http') || product.images.startsWith('/uploads')) {
                return product.images;
            }

            const parsed = JSON.parse(product.images);
            if (Array.isArray(parsed) && parsed.length > 0) {
                return parsed[0];
            }
        } catch {
            // Fallback for non-JSON strings
            if (product.images && (product.images.startsWith('http') || product.images.startsWith('/'))) {
                return product.images;
            }
        }
        return '/images/honey-jar.jpg';
    };

    const fetchProducts = useCallback(async () => {
        setIsLoading(true);
        try {
            const currentParams = new URLSearchParams(searchParams.toString());

            const params: any = {
                per_page: 20,
                // locale needs to be accessed from hook
                lang: locale || 'en'
            };

            if (selectedCategory !== 'all') {
                params.category = selectedCategory; // Woo uses ID or slug depending on setup, usually ID
            }

            // Maps sort options
            if (sortBy === 'price_asc') {
                params.orderby = 'price';
                params.order = 'asc';
            } else if (sortBy === 'price_desc') {
                params.orderby = 'price';
                params.order = 'desc';
            } else {
                params.orderby = 'date';
                params.order = 'desc';
            }

            if (minPrice) params.min_price = minPrice;
            if (maxPrice) params.max_price = maxPrice;

            // Call internal API which wraps Woo Client
            const res = await fetch(`/api/products?${new URLSearchParams(params as any).toString()}`);
            if (res.ok) {
                const data = await res.json();
                setProducts(data.products || []);
            }
        } catch (err) {
            console.error('Failed to fetch products', err);
        } finally {
            setIsLoading(false);
        }
    }, [searchParams, selectedCategory, sortBy, minPrice, maxPrice, locale, t]);

    // Read URL query params on mount or change
    useEffect(() => {
        const cat = searchParams.get('category');
        if (cat) setSelectedCategory(cat);
        else setSelectedCategory('all');
    }, [searchParams]);

    useEffect(() => {
        const fetchCategories = async () => {
            try {
                const res = await fetch('/api/categories');
                if (res.ok) {
                    const data = await res.json();
                    setCategories(data.categories || []);
                }
            } catch (err) {
                console.error('Failed to fetch categories', err);
            }
        };

        fetchCategories();
    }, []);

    // Debounce price filter
    useEffect(() => {
        const timer = setTimeout(() => {
            fetchProducts();
        }, 500);
        return () => clearTimeout(timer);
    }, [fetchProducts]); // Re-fetch on any filter change

    // Derived state for display - no longer client-side sorting!
    const displayProducts = products;

    const displayCategories = [
        { id: 0, name: t('shop.allProducts'), slug: 'all' },
        ...categories
    ];

    return (
        <main className="min-h-screen bg-gray-50 pt-32 lg:pt-40 pb-20 lg:pb-0"> {/* Increased padding for header separation */}
            <CategoryRail />
            <div className="max-w-7xl mx-auto px-6 py-8">
                {/* Filters Bar - Shein Style */}
                <div className="bg-white rounded-lg shadow-sm p-4 mb-6 sticky top-0 z-10"> {/* Removed hidden lg:block to allow Sort/Price on mobile */}
                    <div className="flex flex-col md:flex-row gap-4 items-start md:items-center justify-between">
                        {/* Categories - Hidden as we now use CategoryRail */}
                        <div className="hidden">
                            <div className="flex items-center gap-2 mb-2">
                                <Filter size={16} className="text-gray-400" />
                                <span className="text-xs font-semibold text-gray-500 uppercase">{t('shop.categories')}</span>
                            </div>
                            <div className="flex flex-wrap gap-2">
                                {displayCategories.slice(0, 6).map((cat) => (
                                    <button
                                        key={cat.id}
                                        onClick={() => setSelectedCategory(cat.slug)}
                                        className={`px-4 py-2 rounded-full text-sm font-medium transition-all ${selectedCategory === cat.slug
                                            ? 'bg-black text-white'
                                            : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                                            }`}
                                    >
                                        {cat.name}
                                    </button>
                                ))}
                            </div>
                        </div>

                        {/* Sort & View */}
                        <div className="flex flex-col sm:flex-row gap-4 w-full md:w-auto">
                            {/* Price Inputs */}
                            <div className="flex items-center gap-2">
                                <input
                                    type="number"
                                    placeholder={t('shop.minPrice')}
                                    className="w-20 px-3 py-2 border border-gray-200 rounded-lg text-sm focus:outline-none focus:ring-2 focus:ring-black"
                                    value={minPrice}
                                    onChange={(e) => setMinPrice(e.target.value)}
                                />
                                <span className="text-gray-400">-</span>
                                <input
                                    type="number"
                                    placeholder={t('shop.maxPrice')}
                                    className="w-20 px-3 py-2 border border-gray-200 rounded-lg text-sm focus:outline-none focus:ring-2 focus:ring-black"
                                    value={maxPrice}
                                    onChange={(e) => setMaxPrice(e.target.value)}
                                />
                            </div>

                            <div className="flex items-center gap-2">
                                <select
                                    value={sortBy}
                                    onChange={(e) => setSortBy(e.target.value)}
                                    className="px-4 py-2 border border-gray-200 rounded-lg text-sm focus:outline-none focus:ring-2 focus:ring-black"
                                >
                                    <option value="newest">{t('shop.newest')}</option>
                                    <option value="price-low">{t('shop.priceLow')}</option>
                                    <option value="price-high">{t('shop.priceHigh')}</option>
                                    <option value="name-asc">{t('shop.nameAZ')}</option>
                                    <option value="name-desc">{t('shop.nameZA')}</option>
                                </select>

                                <div className="flex gap-2 border border-gray-200 rounded-lg p-1">
                                    <button
                                        onClick={() => setViewMode('grid')}
                                        className={`p-2 rounded ${viewMode === 'grid' ? 'bg-black text-white' : 'text-gray-400'}`}
                                    >
                                        <Grid size={18} />
                                    </button>
                                    <button
                                        onClick={() => setViewMode('list')}
                                        className={`p-2 rounded ${viewMode === 'list' ? 'bg-black text-white' : 'text-gray-400'}`}
                                    >
                                        <List size={18} />
                                    </button>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>

                {/* Results Count */}
                {!isLoading && (
                    <div className="mb-6">
                        <p className="text-sm text-gray-600">
                            <span className="font-semibold">{displayProducts.length}</span> {t('shop.productsFound')}
                        </p>
                    </div>
                )}

                {/* Products Grid/List */}
                {isLoading ? (
                    <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4">
                        {[...Array(8)].map((_, i) => (
                            <ProductSkeleton key={i} />
                        ))}
                    </div>
                ) : displayProducts.length === 0 ? (
                    <div className="text-center py-20 bg-white rounded-lg flex flex-col items-center justify-center">
                        <ShoppingBag size={48} className="text-gray-300 mb-4" />
                        <h3 className="text-lg font-bold text-gray-900 mb-2">{t('shop.noProducts') || 'No products found'}</h3>
                        <p className="text-gray-500 max-w-md mx-auto mb-6">We couldn&apos;t find any products matching your filters. Try adjusting your search or category.</p>
                        <button
                            onClick={() => {
                                setSelectedCategory('all');
                                setMinPrice("");
                                setMaxPrice("");
                                setSortBy('newest');
                            }}
                            className="px-6 py-2 bg-black text-white rounded-full text-sm font-medium hover:bg-gray-800 transition-colors"
                        >
                            Clear All Filters
                        </button>
                    </div>
                ) : (
                    <div className={viewMode === 'grid'
                        ? 'grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-4'
                        : 'flex flex-col gap-4'
                    }>
                        {displayProducts.map((product) => {
                            // Calculate Discount
                            const hasDiscount = product.compare_at_price && product.compare_at_price > product.price;
                            const discountPercentage = hasDiscount
                                ? Math.round(((product.compare_at_price! - product.price) / product.compare_at_price!) * 100)
                                : null;

                            return (
                                <div
                                    key={product.id}
                                    className={`group bg-white rounded-lg overflow-hidden hover:shadow-xl transition-all duration-300 ${viewMode === 'list' ? 'flex flex-row' : 'flex flex-col'
                                        }`}
                                >
                                    <Link href={`/shop/${product.slug}`} className={viewMode === 'list' ? 'w-48 flex-shrink-0' : 'w-full'}>
                                        <div className={`relative overflow-hidden bg-gray-100 ${viewMode === 'list' ? 'h-full' : 'aspect-square'}`}>
                                            <Image
                                                src={getMainImage(product)}
                                                alt={product.name}
                                                fill
                                                className="object-cover group-hover:scale-110 transition-transform duration-500"
                                                sizes="(max-width: 768px) 50vw, (max-width: 1024px) 33vw, 25vw"
                                            />


                                            {/* Wishlist & Quick View */}
                                            <div className="absolute top-2 right-2 flex flex-col gap-2 opacity-0 group-hover:opacity-100 transition-opacity z-10">
                                                <WishlistButton
                                                    productId={product.id}
                                                    className="p-2 bg-white rounded-full shadow-lg hover:bg-red-50"
                                                />
                                                <button className="p-2 bg-white rounded-full shadow-lg hover:bg-gray-50">
                                                    <Eye size={16} className="text-gray-700" />
                                                </button>
                                            </div>

                                            {/* Badges Container */}
                                            <div className="absolute top-2 left-2 flex flex-col gap-1 z-10">
                                                {/* Stock Badge */}
                                                {product.stock_quantity !== undefined && product.stock_quantity < 5 && product.stock_quantity > 0 && (
                                                    <span className="px-2 py-1 bg-orange-500 text-white text-xs font-bold rounded shadow-md">
                                                        {t('shop.stockLimited')}
                                                    </span>
                                                )}
                                                {/* Sale Badge */}
                                                {hasDiscount && (
                                                    <span className="px-2 py-1 bg-red-600 text-white text-xs font-bold rounded shadow-md">
                                                        -{discountPercentage}%
                                                    </span>
                                                )}
                                            </div>
                                        </div>
                                    </Link>

                                    <div className={`p-4 flex flex-col ${viewMode === 'list' ? 'flex-1' : ''}`}>
                                        <Link href={`/shop/${product.slug}`}>
                                            <span className="text-xs text-gray-500 uppercase tracking-wide mb-1 block">
                                                {product.category_name}
                                            </span>
                                            <h3 className="font-medium text-gray-900 mb-2 line-clamp-2 hover:text-gray-600 transition-colors">
                                                {product.name}
                                            </h3>
                                        </Link>

                                        {/* Rating */}
                                        <div className="flex items-center gap-1 mb-2">
                                            {[...Array(5)].map((_, i) => (
                                                <Star key={i} size={12} className="fill-yellow-400 text-yellow-400" />
                                            ))}
                                            <span className="text-xs text-gray-500 ml-1">(4.8)</span>
                                        </div>

                                        <div className="flex items-center justify-between mt-auto">
                                            <div className="flex flex-col">
                                                <div className="flex items-baseline gap-2">
                                                    <p className="text-xl font-bold text-black">
                                                        {Number(product.price).toFixed(2)}Ôé¼
                                                    </p>
                                                    {hasDiscount && (
                                                        <span className="text-sm text-gray-400 line-through">
                                                            {Number(product.compare_at_price).toFixed(2)}Ôé¼
                                                        </span>
                                                    )}
                                                </div>
                                            </div>

                                            <button
                                                onClick={() => addToCart({
                                                    id: product.id,
                                                    title: product.name,
                                                    price: product.price,
                                                    image: getMainImage(product)
                                                })}
                                                className="p-2 bg-black text-white rounded-full hover:bg-gray-800 transition-colors"
                                            >
                                                <ShoppingBag size={18} />
                                            </button>
                                        </div>
                                    </div>
                                </div>
                            );
                        })}
                    </div>
                )}
            </div>
        </main>
    );
}

export default function ShopPage() {
    return (
        <Suspense fallback={<div className="min-h-screen pt-40 flex justify-center"><p>Loading...</p></div>}>
            <ShopContent />
        </Suspense>
    );
}

